mapper.select:
select kind, id, user_id, order_no, type, symbol, amount,
status,
create_time from t_user_wallet_union_record

	union
	(select 1, id, user_id, order_no, type, symbol,
	amount, status, create_time
	from t_user_record_deposit where user_id = #{userId} and status = 1
	and symbol=#{symbol}
	and create_time >=#{start}
	and create_time <=#{end}
	)
	union
	(select 1, id, user_id, order_no, type, symbol,
	amount, status, create_time
	from t_user_record_transfer where user_id = #{userId} and type = 0 and status = 1
	and symbol=#{symbol}
	and create_time >=#{start}
	and create_time <=#{end}
	)


	union
	(select 2, id, user_id, order_no, type, symbol,
	amount, status, create_time
	from t_user_record_transfer where user_id = #{userId} and type = 1 and status = 1
	and symbol=#{symbol}
	and create_time >=#{start}
	and create_time <=#{end}
	)
	union
	(select 2, id, user_id, order_no, type, symbol,
	amount, status, create_time
	from t_user_record_withdrawal where user_id = #{userId} and status = 1
	and symbol=#{symbol}
	and create_time >=#{start}
	and create_time <=#{end}
	)
